choose.when:
SELECT user_num, user_name, id, nickname, pic_path, avg_rate
FROM user
WHERE address LIKE CONCAT('%', #{blonSggName}, '%') AND avg_rate IS NOT NULL AND user_num != #{userNum}
ORDER BY avg_rate DESC
LIMIT 5;

choose.otherwise:
SELECT user_num, user_name, id, nickname, pic_path, avg_rate
FROM user
WHERE avg_rate IS NOT NULL AND user_num != #{userNum}
ORDER BY avg_rate DESC
LIMIT 5;
